function Index() {
  const [state, setState] = useState({ open: false });
  // const [selection, setSelection] = useState({
  //   productID: "",
  //   productName: "",
  // });
  const [selection, setSelection] = useState();

  function handlSelection(resouces) {
    // const idFromResources = resouces.selection.map((products) => product);
    // setState({ open: false });
    // setSelection({
