import React, { useState } from "react";
import { Heading, Page } from "@shopify/polaris";
import { ResourcePicker } from "@shopify/app-bridge-react";

// const Index = () => (
//   <Page>
//     <Heading>Shopify app with Node and React ðŸŽ‰</Heading>
//   </Page>
// );
function Index() {
  const [state, setState] = useState({ open: false });

  function handlSelection(resouces) {
    const idFromResources = resouces.selection.map((product) => product.id);
    setState({ open: false });
    console.log("idFromResources: ", idFromResources);
  }
  return (
    <>
      <Page
        title="Product selector"
        primaryAction={{
          content: "Select Products",
          onAction: () => setState({ open: true }),
        }}
      >
        <ResourcePicker
          resourceType="Product"
          open={state.open}
          onCancel={() => setState({ open: false })}
          onSelection={(resouces) => handlSelection(resouces)}
        />
      </Page>
    </>
  );
}

export default Index;
